import React from "react";
import ReadMoreReact from "read-more-react";

export const Text = ({ text }) => {
  return (
    <div className="row">
      <div className="col">
        <ReadMoreReact
          text={text}
          min={150}
          ideal={350}
          max={2000}
          readMoreText="click here to read more"
        />
        
      </div>
    </div>
  );
};
