import React from "react";
import { Poster } from "../media/Poster";

export const MovieTile = ({ id, image, title, year, character, mediaType}) => {
    return(
        <article className="row">
            <div className="col-2">
                <a href={`/movie/${id}`}>
                    <Poster path={image} alt={title} size={"small"} styles={"cast-img"}/>
                </a>                
            </div>

            <div className="col-10">
            <a href={`/movie/${id}`}><h4>{title}</h4></a>

            <p className="info-values fs-5"><i>{ character }</i></p>            
                <p className="info-values fs-6"><i>{ year } <span>{ type }</span></i></p> 
               

            </div>
        </article>

    )
}
