import React, { useState, useEffect } from "react";
import { SearchBar } from "../../components/search/SearchBar";
import { multiSearch, getTrendingMovies, getTrendingTvShows } from "../../controllers/api";
import { CardList } from "../../containers/CardList";
import { Hero } from "../../containers/hero/Hero";
import { SectionTitle } from "../../components/section/SectionTitle";

export const HomePage = () => {
  //search results
  const [searchTerm, setSearchTerm] = useState("");
  const [searchResults, setSearchResults] = useState(null);

  const handleSearch = async (e) => {
    e.preventDefault();
    const results = await multiSearch(searchTerm);
    setSearchResults(results);
    setSearchTerm("");
  };

  console.log(searchResults);

  //trending movies
  const [trendingMovies, setTrendingMovies] = useState(null);

  useEffect(() => {
    async function getTrendingData() {
      const trendingData = await getTrendingMovies();
      setTrendingMovies(trendingData);
    }
    getTrendingData();
  }, []);

  return (
    <>
      <div className="contaner">
        {/* <SearchBar
          searchTerm={searchTerm}
          setSearchTerm={setSearchTerm}
          handleSearch={handleSearch}
        /> */}
        <Hero />
      </div>

      {searchResults ? 
      <div className="container">
            <SectionTitle name={"Results"} />
            <CardList items={searchResults} />
        </div> : ""}

      

      {trendingMovies? 
      <div className="container">
            <SectionTitle name={"Now Trending "} />
            <CardList items={trendingMovies} />
      </div>: ""}
    </>
  );
};
