import React from "react";
import { posterBaseURLMedium } from "../../utils/mediaUrls";
import { posterBaseURLSmall } from "../../utils/mediaUrls";

export const Poster = ({ path, alt, size }) => {
  let posterSizeUrl;

  if(size === "small"){
    posterSizeUrl = posterBaseURLSmall
  } else if( size === "medium") {
    posterSizeUrl = posterBaseURLSmall
  };

  return (
    // <img src={`${posterBaseURLSmall}${path}`} alt={alt} className="poster-img img-thumbnail"/>

    <img
      src={
        path
          ? posterSizeUrl + path
          : "https://cdn.pixabay.com/photo/2020/08/13/09/45/photo-5484713_960_720.png"
      }
      alt={alt}
      className="poster-img img-thumbnail"
    />
  );
};
