import React from "react";
import { PersonCard } from "../../components/cards/PersonCard";

export const PeopleContainer = ({ cast, image, name, characterName, id }) => {
  //show only the first 12 actors
  return cast.slice(0, 12).map((person) => {
    return (
      <div className="row">
        <PersonCard
          key={id}
          id={id}
          photoPath={image}
          name={name}
          character={characterName}
        />
      </div>
    );
  });
};
