import React from "react";
import { Poster } from "../media/Poster";

export const PersonCard = ({photoPath, name, character, id}) => {
    return(
       <>
       <div className="col-2">
            <a href={`/people/${id}`}>
                {/* <img className="cast-img my-2" src={`${posterBaseURLSmall}${photo}`}/>  */}
                <Poster path={photoPath} alt={name} size={"small"} className="cast-img my-2"/>         
            </a> 
       </div>

       <div className="col-4">           
            <a href={`/people/${id}`}>
              {name}
            </a>               
            <p className="info-values">{character}</p>           
       </div>           
           
        </>        
 
    )

}
