import React from "react";
import { CircularProgressbar, buildStyles } from "react-circular-progressbar";
import "react-circular-progressbar/dist/styles.css";

export const RatingsBar = ({ number }) => {
    let color;
    if(number > 7) {
        color = "green"
    } else if( number > 4) {
        color = "yellow"
    } else {
        color = "red"
    }

  return (
    <div style={{ width: 70, height: 70 }}>
      <CircularProgressbar
        text={number}
        minValue={0}
        maxValue={10}
        value={number}
        styles={buildStyles({
            textSize: "1.75rem",
            textColor: "white",
            pathColor: color
          })}
      />
    </div>
    // </div>
  );
};
