        className="form-control py-2"
        type="text"
        placeholder="Search for Movies, Actors or TV Shows "
        style={inputStyles}
        value={userQuery}
        onChange={(e) => setUserQuery(e.target.value)}
      />

      <button
        className="btn btn-light px-4 search-btn"
        type="submit"
        onClick={handleClick}
        style={buttonStyles}
      >
        Search
