import React from "react";
import { posterBaseURLMedium } from "../../utils/mediaUrls";
import { Link } from "react-router-dom";
//import { AddButton } from "../AddButton/AddButton";

export const Card = ({ title, poster, ratings, populatiry, date, id}) => {
    return(
        <div className="card movie-card" style={{width: "15rem"}}>   
            <a  key={id}
                href={`/movie/${id}`}>       
                   
                <img 
                src={ poster 
                        ? posterBaseURLSmall + poster
                        : "https://images.unsplash.com/photo-1538152911114-73f1aa6d6128?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=975&q=80"
                    }
                alt={title}
                className="movie-thumb card-img-top"/>

                <div className="movie-info-wrapper card-body">
                    <h2 className="title">{title}</h2>
                                                  
                    <p className="info-text">Ratings <span className="info-values">{ratings}</span></p>
                    <p className="info-text">Popularity <span className="info-values">{populatiry}</span></p> 
                    <p className="info-text">Realease Date <span className="info-values">{date}</span></p> 
                    
                </div>                    
            </a>
            {/* <AddButton /> */}
        </div>
    );
}
