import React, { useState, useEffect } from "react";
import { getDetails, getVideos } from "../../controllers/api";
import { posterBaseURL, youtubeBaseURL } from "../../utils/mediaURLs";
import { Video } from "../../Components/Video/Video";
import { useParams } from "react-router-dom";

import { SectionTitle } from "../../Components/SectionTitle/SectionTitle";
import { Heading } from "../../Components/Results/Headers/Heading";
import { RatingsInfo } from "../../Components/Results/Headers/RatingsInfo";
import { Media } from "../../Components/Results/Media/Media";
import { Info } from "../../Components/Results/Info/Info";
import { Categorie } from "../../Components/Results/Categories/Categories";
import { Cast } from "../../Components/Results/Cast/Cast";
import { MovieCart } from "../../Components/MovieCart/MovieCart";

import { formatRuntime } from "../../utils/utils";

export const MoviePage = () => {
  //get the id of the clicked movie
  const { id } = useParams();
  const [movieID, setMovieID] = useState(id);
  const [movie, setMovie] = useState(id);

  //with appended to response
  const [cast, setCast] = useState(null);
  const [images, setImages] = useState(null);
  
  //  const [videos, setVideos] = useState(null);
  const [similar, setSimilar] = useState(null);

  useEffect(() => {
    async function getMovieDetails() {
      const movieDetails = await getDetails(movieID);
      setMovie(movieDetails);
      setCast(movieDetails.credits.cast);
      setSimilar(movieDetails.similar);
      // console.log(movieDetails.similar)
    }
    getMovieDetails();
  }, []);

  console.log(similar);

  //Video
  const [videos, setVideos] = useState({});

  useEffect(() => {
    async function getVideosDetails() {
      const videos = await getVideos(movieID);
      //get the fist video key //FIX LATER
      const key = videos[0].key;
      setVideos(key);
    }
    getVideosDetails();
  }, [movieID]);

  //Genres
  const [genres, setGenres] = useState([]);
  useEffect(() => {
    if (movie) {
      setGenres(movie.genres);
    }
  }, [movie]);

  return (
    <div className="container">
      <Heading name={movie.title} />
      <RatingsInfo runtime={movie.runtime} status={movie.status} />
      <Media posterPath={movie.poster_path} videoKey={videos} />

      <div className="row">
        <div className="col my-4">
          {genres ? (
            genres.map((genre) => {
              return <Categorie categorie={genre.name} key={genre.name} />;
            })
          ) : (
            <p>no genres info</p>
          )}
        </div>
      </div>

      <Info overview={movie.overview} tagline={movie.tagline} />

      <SectionTitle name={"Top Cast"} />

      <div className="row">
        {cast ? (
          cast.slice(0, 12).map((member) => {
            return (
              <Cast
                key={member.id}
                id={member.id}
                photo={member.profile_path}
                name={member.name}
                character={member.character}
              />
            );
          })
        ) : (
          <p>no cast data</p>
        )}
      </div>

      <div className="row">
        <SectionTitle name={"Similar Movies"} />
        {similar ? (
          similar.results.map((movie) => {
            return (
              <MovieCart
                key={movie.id}
                title={movie.title}
                id={movie.id}
                poster={movie.poster_path || movie.backdrop_path}
                overview={movie.overview}
                ratings={movie.vote_average}
                populatiry={movie.popularity}
                release={movie.release_date}
              />
            );
          })
        ) : (
          <p>no similar movies</p>
        )}
      </div>
    </div>
  );
};
