import React from "react";
import { Poster } from "../media/Poster";

export const PersonCard = ({ photoPath, name, character, id, imageType }) => {
  return (
    <div className="row">
      <a href={`/people/${id}`}>
        <Poster
          path={photoPath}
          alt={name}
          size={"small"}        
          imageType={"cast-img my-2"}
        />
      </a>
      <a href={`/people/${id}`}>{name}</a>
      <p className="info-values">{character}</p>
    </div>
  );
};
